
use Bookstore;
go
declare @SachID int, @GiaGoc money; 
declare ChiTietSach_Cursor CURSOR --Khai báo con tr? tr? ??n k?t qu? tr? v? c?a select
for 
	select MaSach, GiaGoc
	from Book.ChiTietSach
open ChiTietSach_Cursor; 
fetch next from ChiTietSach_Cursor into @SachID, @GiaGoc;
while @@FETCH_STATUS <> -1 --@@FETCH_STATUS = 0 thì d? li?u v?n còn ??c ???c, ch?a ??c t?i cu?i b?ng
	begin 
		fetch next from ChiTietSach_Cursor into @SachID, @GiaGoc;
	end;
close ChiTietSach_Cursor; --?óng con tr?
deallocate ChiTietSach_Cursor; --Gi?i phóng tài nguyên
go